DROP DATABASE IF EXISTS BOOKSTORE;
CREATE DATABASE BOOKSTORE;

USE BOOKSTORE;

SET NAMES utf8mb4;

DELIMITER $$
--
-- Thủ tục
--

CREATE PROCEDURE `edit_kh` (IN `mskh` INT(10), IN `ten` VARCHAR(30), IN `diachi` VARCHAR(30), IN `sdt` CHAR(11))   BEGIN
UPDATE khachhang kh SET kh.HoTenKH=ten, kh.DiaChi=diachi ,kh.SoDienThoai=sdt WHERE kh.MSKH=mskh;
END$$

DELIMITER ;

-- --------------------------------------------------------

--
-- Cấu trúc bảng cho bảng `chitietdathang`
--

DROP TABLE IF EXISTS `chitietdathang`;
CREATE TABLE `chitietdathang` (
  `SoDonDH` int NOT NULL,
  `MSHH` int NOT NULL,
  `SoLuong` int DEFAULT '1',
  `GiaDatHang` decimal(10,2) DEFAULT '0.00'
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci ROW_FORMAT=DYNAMIC;

